import styled from 'styled-components';
import * as GS from '../../styles/global';
import { motion } from 'framer-motion';

export const Call = styled(GS.FlexEven)`
	& > div {
		text-align: center;
	}
	.x {
		font-size: 3rem;
		font-weight: 300;
		font-family: 'Times New Roman', Times, serif;
	}
`;
