import styled from 'styled-components';
import * as GS from '../../styles/global';
import { motion } from 'framer-motion';

export const HeroBackground = styled(GS.FullWidth)`
	background: #eee;
	width: 100vw;
	height: 100vh;
	position: relative;
`;

export const Message = styled.div`
	max-width: 70rem;
	text-align: center;
	margin: 0 auto;
	background: #888;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
`;

export const Form = styled.div`
	width: 100%;
	position: absolute;
	bottom: 0;
	left: 0;
	text-align: center;
	background: rgba(0, 0, 0, 0.5);
	input {
		padding: 0.75rem;
		margin-right: 1rem;
	}
	button {
		background: #f84f49;
		padding: 1.22rem 0.75rem;
		border: 0;
	}
`;
