import styled from 'styled-components';
import * as GS from '../../styles/global';
import { motion } from 'framer-motion';

export const HeroBackground = styled(GS.FullWidth)`
	background: #eee;
	width: 100vw;
	height: 100vh;
	position: relative;
`;

export const Message = styled.div`
	max-width: 70rem;
	text-align: center;
	margin: 0 auto;
	background: #888;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
`;

export const Form = styled.div`
	width: 100%;
	position: relative;
	bottom: 0;
	left: 0;
	text-align: center;
	background: rgba(0, 0, 0, 0.5);
`;

export const FormContainer = styled.form`
	display: flex;
	gap: 5rem;
	.name {
		flex: 1;
	}
	.email {
		flex: 1.75;
	}
	.submit-button {
		flex: 1;
	}
	& > div {
		input {
			padding: 0.75rem;
			margin-right: 1rem;
			width: 100%;
		}
		button {
			background: #f84f49;
			padding: 1.2rem 3rem;
			font-weight: bold;
			color: #fff;
			width: 100%;
			border: 0;
		}
	}
`;
