import * as S from './hero.styles';
import * as GS from '../../styles/global';
import Link from 'next/link';
import { IoIosArrowDown } from 'react-icons/io';

const Hero = ({ message, href, link, arrow, background, tags }) => {
	return (
		<S.HeroBackground background={background}>
			<GS.MaxContainer>
				<S.Message>
					<h1>{message}</h1>
					{href && (
						<Link href={href} passHref>
							<GS.SecondaryButton>{link}</GS.SecondaryButton>
						</Link>
					)}
					{message === 'Blog' && (
						<>
							<ul>
								<li>Hello</li>
								<li>World!</li>
							</ul>
							<S.Tags>
								{tags.tags.map((tag) => {
									<li key={tag.slug}>
										<a>{tag.slug}</a>
									</li>;
								})}
							</S.Tags>
						</>
					)}
				</S.Message>
				{arrow && (
					<GS.Arrow>
						<IoIosArrowDown />
					</GS.Arrow>
				)}
			</GS.MaxContainer>
		</S.HeroBackground>
	);
};

export default Hero;
