import styled from 'styled-components';
import * as GS from '../../styles/global';
import { motion } from 'framer-motion';

export const HeroBackground = styled(GS.FullWidth)`
	background: red;
	width: 100vw;
	height: 100vh;
`;

export const Message = styled.div`
	max-width: 100rem;
	text-align: center;
	margin: 0 auto;
`;
