import Layout from '../../../components/layout/layout';
import { getContent } from '../../../lib/ghost';
import * as GS from '../../../styles/global';

export default async function Slug({ data }) {
	const slug = window.location.pathname;
	try {
		const response = await fetch(
			`${process.env.GHOST_URL}/ghost/api/v3/content/posts/slug${slug}/?key=${process.env.GHOST_KEY}`
		);
		const data = await response.json();
		return data;
	} catch (error) {
		console.log(error);
	}
	return (
		<>
			<Layout>Hello</Layout>
		</>
	);
}
