export async function getServerSideProps(context) {
	const res = await Axios.get(
		`https://localhost:5000/${context.params.topics
			.split(' ')
			.join('%20')}/${context.params.content.split(' ').join('%20')}`
	);
	return {
		props: {
			data: res.data,
		},
	};
}
