import * as S from './bloggy.styles';
import * as GS from '../../styles/global';
import Link from 'next/link';
import Title from '../title';

const Bloggy = ({ posts }) => {
	console.log(posts);

	const timeConvert = (time) => {
		time = time.slice(0, 10);
		return time;
	};

	return (
		<S.BlogContainer>
			{/* Left Side */}
			<S.BlogArticles>
				{posts.map((post) => {
					return (
						<Link href={post.slug} key={post.excerpt}>
							<a>
								<S.Blog>
									<li>
										<img
											src={post.feature_image}
											alt={`From The Ground Up - ${post.title}`}
										/>
									</li>
									<li>
										<h2>{post.title}</h2>
									</li>
									<li>{post.excerpt}</li>
								</S.Blog>
							</a>
						</Link>
					);
				})}
			</S.BlogArticles>

			{/* Right Side */}
			<S.FeaturedArticles>
				<Title title="The Latest" />
				{posts.map((post) => {
					return (
						<S.TopBlogs key={post.feature_image}>
							<div>
								<img src={post.feature_image} />
							</div>
							<div>
								<div>{post.title}</div>
								<ul className="tag">
									{post.tags.map((tag) => {
										return (
											<li key={tag.id}>
												<Link href={tag.slug}>
													<a>{tag.name}</a>
												</Link>
											</li>
										);
									})}
								</ul>
								<div className="updated">Updated {timeConvert(post.updated_at)}</div>
							</div>
						</S.TopBlogs>
					);
				})}
			</S.FeaturedArticles>
		</S.BlogContainer>
	);
};

export default Bloggy;
