import styled from 'styled-components';
import * as GS from '../../styles/global';
import { motion } from 'framer-motion';

export const BlogContainer = styled(GS.FlexEven)`
	align-items: stretch;
	justify-content: center;
	margin-top: 6.8rem;
	gap: 3rem;
	& > div:nth-child(1) {
		flex: 2;
	}
	& > div:nth-child(2) {
		flex: 1;
	}
`;

export const Blog = styled.ul`
	margin-bottom: 5rem;
	li {
		img {
			width: 100%;
			height: auto;
		}
	}
`;
