import Layout from '../../../components/layout/layout';
import { getContent } from '../../../lib/ghost';
import * as GS from '../../../styles/global';

export default function Slug({ content }) {
	console.log(content);
	return (
		<>
			<Layout>Hello</Layout>
		</>
	);
}

export async function getStaticProps() {
	const href = window.location.pathname;
	const slug = href.substring(href.lastIndexOf('/') + 1);

	try {
		const response = await fetch(
			`${process.env.GHOST_URL}/ghost/api/v3/content/posts/slug/${slug}/?key=${process.env.GHOST_KEY}`
		);
		const data = await response.json();
		return data;
	} catch (error) {
		console.log(error);
	}

	return {
		props: {
			content: response,
		},
	};
}
