import styled from 'styled-components';
import * as GS from '../../styles/global';
import { motion } from 'framer-motion';

export const BlogContainer = styled(GS.FlexEven)`
	align-items: stretch;
	justify-content: center;
	margin-top: 7rem;
	gap: 3rem;
	& > article:nth-child(1) {
		flex: 2;
	}
	& > article:nth-child(2) {
		flex: 0.75;
	}
`;
