import Head from 'next/head';
import styled from 'styled-components';
import Layout from '../components/layout/layout';
import * as GS from '../styles/global';
import Hero from '../components/hero';

export default function Academy() {
	return (
		<>
			<Layout>
				<Hero
					message="From the Ground Up Academy"
					background={`url('/ftgu-academy-header.jpg')`}
					link="Sign Up Now"
					href="/"
				/>
				<GS.MaxContainer>
					<GS.YYPadding>
						<GS.FlexEven>
							<div>
								<h2>Our Training</h2>
								<p>
									Our online training is an in-depth expansion of our YouTube channel and is
									designed to help you navigate the apparel industry from A-Z. With
									worksheets to assist you throughout the training, our goal is to give you
									all the tools you need to successfully launch and grow your brand.
								</p>
								<p>
									In addition to the online training you also get access to a private group,
									and monthly training calls to get your questions answered in real time.
									This program is for those who are ready to take action and see results.
								</p>
								<GS.MainButton>Join Today</GS.MainButton>
							</div>
							<div style={{ position: 'relative' }}>
								<GS.PrimaryBlock
									width={'25rem'}
									height={'35rem'}
									borderRadius={'3rem'}
									top={'4rem'}
									left={'20rem'}
								/>
								<Profile src="/ftgu-john-academy.webp" width="400" height="571" />
							</div>
						</GS.FlexEven>
					</GS.YYPadding>
				</GS.MaxContainer>
				<GS.FullWidth>
					<GS.ImageBackground background={`url('/ftgu-academy-man.webp')`}>
						<GS.MaxContainer>
							<GS.FlexEven>
								<div>
									<BigText>
										<div>10X</div>
										<div>Clarity</div>
									</BigText>
								</div>
								<div>
									<p style={{ color: '#fff' }}>
										After navigating the industry from manufacturing, to distribution, and
										eCommerce, we realized that most training programs are unclear or
										don’t even show the logistics of getting things properly made and
										launched in the market. After going through this course you will have
										clarity about all facets of the apparel industry, and will be better
										prepared to grow your brand.
									</p>
									<GS.SecondaryButton>Sign Up Now</GS.SecondaryButton>
								</div>
							</GS.FlexEven>
						</GS.MaxContainer>
					</GS.ImageBackground>
				</GS.FullWidth>
			</Layout>
		</>
	);
}

const BigText = styled.div`
	color: #fff;
	text-align: center;
	& > div:nth-child(1) {
		font-size: 10rem;
		font-family: ${(props) => props.theme.fonts.header};
	}
	& > div:nth-child(2) {
		font-size: 4rem;
		font-family: ${(props) => props.theme.fonts.header};
	}
`;

const Profile = styled.img`
	width: 30rem;
	height: auto;
	float: right;
	position: relative;
	border-radius: 1rem;
`;
