import styled from 'styled-components';
import { motion } from 'framer-motion';

export const stagger = {
	initial: {
		opacity: 0,
	},
	animate: {
		opacity: 1,
		transition: {
			default: {
				staggerChildren: 0,
			},
		},
	},
};

export const fadeIn = {
	initial: {
		opacity: 0,
	},
	animate: {
		opacity: 1,
		transition: {
			delay: 1,
		},
	},
};

export const fadeInTop = {
	initial: {
		opacity: 0,
		y: -100,
	},
	animate: {
		opacity: 1,
		y: 0,
		transition: {
			delay: 0.5,
		},
	},
};

export const fadeInRight = {
	initial: {
		opacity: 0,
		x: -100,
	},
	animate: {
		opacity: 1,
		x: 0,
		transition: {
			delay: 1.5,
		},
	},
};

export const exit = {
	initial: {
		opacity: 1,
	},
	animate: {
		opacity: 0,
	},
};

//global standalone side padding
export const GlobalPadding = styled.div`
	padding: 0rem ${(props) => props.theme.padding};
`;
//global standalone side padding
export const YPadding = styled.div`
	padding: ${(props) => props.theme.padding} 0rem;
`;

//max-container
export const MaxContainer = styled.div`
	padding: 0rem ${(props) => props.theme.padding};
	max-width: ${(props) => props.theme.maxWidth};
	margin: 0 auto;
`;

//mid-container
export const MidContainer = styled.div`
	padding: 0rem ${(props) => props.theme.padding};
	max-width: ${(props) => props.theme.midWidth};
	margin: 0 auto;
`;

//small-container
export const SmContainer = styled.div`
	padding: 0rem ${(props) => props.theme.padding};
	max-width: ${(props) => props.theme.smWidth};
	margin: 0 auto;
`;

//full-width
export const FullWidth = styled.div`
	width: 100%;
	margin: 0 auto;
`;

//flex 50/50 split
export const FlexEven = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	@media screen and (max-width: ${(props) => props.theme.midWidth}) {
		display: block;
	}
	div {
		flex: 1;
	}
`;

//flex 1/3 split
export const FlexOneThree = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	@media screen and (max-width: ${(props) => props.theme.midWidth}) {
		display: block;
	}
	div:nth-child(1) {
		flex: 1;
	}
	div:nth-child(2) {
		flex: 2;
	}
`;

//flex 3/1 split
export const FlexThreeOne = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	@media screen and (max-width: ${(props) => props.theme.midWidth}) {
		display: block;
	}
	div:nth-child(1) {
		flex: 2;
	}
	div:nth-child(2) {
		flex: 1;
	}
`;

//3 column grid
export const GridThree = styled.div`
	display: grid;
	grid-gap: 2rem;
	transition: all 0.25s ease-in-out;
	@media screen and (max-width: ${(props) => props.theme.midWidth}) {
		grid-template-columns: repeat(2, 1fr);
	}
	@media screen and (max-width: ${(props) => props.theme.smWidth}) {
		display: block;
		div {
			margin: ${(props) => props.theme.padding} 0rem;
		}
	}
`;

//2 column grid
export const GridTwo = styled.div`
	display: grid;
	grid-gap: 2rem;
	transition: all 0.25s ease-in-out;
	@media screen and (max-width: ${(props) => props.theme.smWidth}) {
		display: block;
		div {
			margin: ${(props) => props.theme.padding} 0rem;
		}
	}
`;

//main button
export const MainButton = styled(motion.a)`
	padding: 0.5rem 1.5rem;
	text-decoration: none;
	transition: all 0.25s ease-in-out;
	color: ${(props) => props.theme.colors.primary};
	border: 0.3rem solid ${(props) => props.theme.colors.primary};
	display: inline-block;
	margin: 1rem 0rem;
	&:hover {
		background: ${(props) => props.theme.colors.primary};
		text-decoration: none;
		cursor: pointer;
		color: #fff;
	}
`;

//flexcenter
export const FlexCenter = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	margin: 0 auto;
	height: auto;
`;

//used to describe portfolio pieces
export const Meta = styled(motion.div)`
	display: flex;
	align-items: flex-start;
	max-width: 80rem;
	margin: 0 auto;
	div {
		padding-right: ${(props) => props.theme.padding};
		&:nth-child(1) {
			flex: 1.2;
		}
		&:nth-child(2) {
			flex: 1.2;
		}
		&:nth-child(3) {
			flex-basis: auto;
			min-width: 9rem;
			max-width: 10rem;
			padding-right: 0;
		}
	}
	ul {
		li {
			display: inline;
			margin-right: 1.5rem;
			img {
				transition: all 0.25s ease-in-out;
				&:hover {
					transform: scale(1.1);
				}
			}
		}
		margin-bottom: ${(props) => props.theme.padding};
	}
	@media screen and (max-width: ${(props) => props.theme.midWidth}) {
		display: block;
		ul {
			margin-bottom: 1rem;
		}
	}
`;

export const PortfolioWrapper = styled.div`
	max-width: ${(props) => props.theme.maxWidth};
	margin: 0 auto;
	display: flex;
	justify-content: center;
	.portfolio-content {
		flex: 1;
		height: auto;
	}
`;

export const Button = styled(MainButton)`
	text-align: center;
	width: 100%;
`;

export const Block = styled.div`
	position: absolute;
	z-index: -0;
	transition: background 0.25s ease-in-out;
	width: ${(props) => props.width};
	height: ${(props) => props.height};
	bottom: ${(props) => props.bottom};
	left: ${(props) => props.left};
	background: ${(props) => props.theme.colors.block};
`;

export const ScrollContainer = styled.div`
	scroll-snap-type: y mandatory;
	height: 100vh;
	overflow: scroll;
	& > .scroll-child {
		scroll-snap-align: start;
	}
`;
