import * as S from './bloggy.styles';
import * as GS from '../../styles/global';
import Link from 'next/link';

const Bloggy = ({ posts }) => {
	console.log(posts);

	const timeConvert = (time) => {
		time = time.slice(0, 9);
		return time;
	};

	return (
		<S.BlogContainer>
			{/* Left Side */}
			<div>
				{posts.map((post) => {
					return (
						<S.Blog key={post.excerpt}>
							<li>
								<img src={post.feature_image} />
							</li>
							<li>{post.title}</li>
							<li>{post.excerpt}</li>
						</S.Blog>
					);
				})}
			</div>

			{/* Right Side */}
			<div>
				{posts.map((post) => {
					return (
						<S.TopBlogs key={post.feature_image}>
							<div>
								<img src={post.feature_image} />
							</div>
							<div>
								<div>{post.title}</div>
								<ul className="tag">
									{post.tags.map((tag) => {
										return (
											<li key={tag.id}>
												<Link href={tag.slug}>
													<a>{tag.name}</a>
												</Link>
											</li>
										);
									})}
								</ul>
								<div className="updated">{timeConvert(post.updated_at)}</div>
							</div>
						</S.TopBlogs>
					);
				})}
			</div>
		</S.BlogContainer>
	);
};

export default Bloggy;
