		transition: {
			default: {
				staggerChildren: 0,
			},
		},
	},
};

export const fadeIn = {
	initial: {
		opacity: 0,
	},
	animate: {
		opacity: 1,
		transition: {
			delay: 1,
		},
	},
};

export const fadeInTop = {
	initial: {
		opacity: 0,
		y: -100,
	},
	animate: {
		opacity: 1,
