	display: flex;
	justify-content: center;
	align-items: center;
	@media screen and (max-width: ${(props) => props.theme.midWidth}) {
		display: block;
	}
	div:nth-child(1) {
		flex: 2;
	}
	div:nth-child(2) {
		flex: 1;
	}
`;

//3 column grid
export const GridThree = styled.div`
	display: grid;
	grid-gap: 2rem;
	transition: all 0.25s ease-in-out;
	@media screen and (max-width: ${(props) => props.theme.midWidth}) {
