import * as S from './bloggy.styles';
import * as GS from '../../styles/global';
import SidebarMail from '../sidebarmail';
import Link from 'next/link';
import Title from '../title';

const Bloggy = ({ posts }) => {
	console.log(posts);

	const timeConvert = (time) => {
		time = time.slice(0, 10);
		return time;
	};

	return (
		<S.BlogContainer>
			{/* Left Side */}
			<S.BlogArticles>
				{posts &&
					posts.map((post) => {
						return (
							<Link href={`/blog/${encodeURIComponent(post.slug)}`} key={post.excerpt}>
								<a>
									<S.Blog>
										<li>
											<img
												src={post.feature_image}
												alt={`From The Ground Up - ${post.title}`}
											/>
										</li>
										<li>
											<h2>{post.title}</h2>
										</li>
										<li>{post.excerpt}</li>
									</S.Blog>
								</a>
							</Link>
						);
					})}

				{post && (
					<>
						<img src={post.feature_image} alt={`From The Ground Up - ${post.title}`} />
					</>
				)}
			</S.BlogArticles>

			{/* Right Side */}
			<S.FeaturedArticles>
				<Title title="Top Articles" />
				{posts &&
					posts.map((post) => {
						return (
							<S.TopBlogs key={post.feature_image}>
								<div>
									<Link href={`/blog/${encodeURIComponent(post.slug)}`} key={post.excerpt}>
										<a>
											<img src={post.feature_image} />
										</a>
									</Link>
								</div>
								<div>
									<div>{post.title}</div>
									<ul className="tag">
										{post.tags.map((tag) => {
											return (
												<li key={tag.id}>
													<Link
														href={`/blog/${encodeURIComponent(tag.slug)}`}
														key={post.excerpt}
													>
														<a>{tag.name}</a>
													</Link>
												</li>
											);
										})}
									</ul>
									<div className="updated">Updated {timeConvert(post.updated_at)}</div>
								</div>
							</S.TopBlogs>
						);
					})}
				<SidebarMail />
			</S.FeaturedArticles>
		</S.BlogContainer>
	);
};

export default Bloggy;
